import React, {useState} from "react";
import axios from "axios";

const SignUpForm = (props) => {

  const [userData, setUserData] = useState({ fullName: "", email: "", password: "" });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSignUp = () => {
    axios.post("/api/register", userData)
      .then(response => {
        // Handle successful registration, e.g., show a success message
        console.log("User registered successfully");
        props.toggleSignup();
      })
      .catch(error => {
        // Handle registration errors, e.g., show an error message
        console.error("Error registering user", error);
      });
  };

  return (
    <div className="d-flex justify-content-center align-items-center" style={{ minHeight: "100vh" }}>
      <form className="border p-4 rounded shadow">
        <h2 className="text-center mb-4">Create Account</h2>
        <div className="mb-3">
          <label htmlFor="fullName" className="form-label">Full Name</label>
          <input type="text" className="form-control" id="fullName" placeholder="Enter your full name" />
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input type="email" className="form-control" id="email" placeholder="Enter your email" />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password</label>
          <input type="password" className="form-control" id="password" placeholder="Enter your password" />
        </div>
        <div className="row">
            <div className="col-6 d-flex justify-content-center">
            <button type="submit" className="btn btn-primary btn-sm" onClick={props.togglelog}>Sign Up</button>
            </div>
            <div className="col-6 d-flex justify-content-center" >
            <button className="btn btn-primary btn-sm" onClick={props.toggleSignup}>Close</button>
            </div>
        </div>
      </form>
    </div>
  );
};

export default SignUpForm;
