import React, { useState } from "react";

const NewsContainer = (props) => {

    const Add = () => {
        const token = localStorage.getItem("token");
        if (token) {
            fetch('http://localhost:5000/api/bookmarks', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    title: props.title,
                    description: props.description,
                    url: props.url,
                    urlToImage: props.urlToImage,
                }),
            })
                .then((response) => {
                    if (response.ok) {
                        return response.json();

                    }
                    throw new Error('Authentication failed');
                })
                .then((data) => {
                    console.log(data);
                })
                .catch((error) => console.error(error));
        }
    };
    const Delete = () => {
        const token = localStorage.getItem("token");
        if (token) {
            fetch(`http://localhost:5000/api/bookmarks/${props._id}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            })
                .then((response) => {
                    if (response.ok) {
                        return response.json();
                    }
                    throw new Error('Authentication failed');
                })
                .then((data) => {
                    console.log(data);
                })
                .catch((error) => console.error(error));
        }
    };


    const HandleClick = () => {
        const token = localStorage.getItem("token");
        if (token) {
            fetch('http://localhost:5000/api/bookmarks', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            })
                .then((response) => {
                    if (response.ok) {
                        return response.json();
                    }
                    throw new Error('Authentication failed');
                })
                .then((data) => {
                    if (data.some((bookmark) => bookmark.title === props.title)) {
                        Delete();
                    } else {
                        Add();
                    }
                })
                .catch((error) => console.error(error));
        }
    }

    const HandleDate = () => {
        const date = new Date(props.publishedAt);
        const day = date.getDate();
        const month = date.getMonth() + 1;
        const year = date.getFullYear();
        return `${day}/${month}/${year}`;
    }

    

    return(
        <div class="col">
            <div class="card shadow-sm">
              <img src={props.urlToImage} class="bd-placeholder-img card-img-top"  width="100%" height="225" alt=""/>
              <div class="card-body">
                <h6 class="card-text">{props.title}</h6>
                <small class="text-body-secondary">{props.description}</small>
                <div class="d-flex justify-content-between align-items-center">
                  <div class="btn-group">
                    <a href={props.url}> <button type="button" class="btn btn-sm btn-outline-secondary">View</button></a>
                    <button type="button" class="btn btn-sm btn-outline-secondary"><img src="images/bookmark-white.png" alt="bookmark" width="20px" height="20px" onClick={HandleClick}/></button>
                  </div>
                  <small class="text-body-secondary">{HandleDate}</small>
                </div>
              </div>
            </div>
          </div>
    );
};

export default NewsContainer;
