import React, { useState } from "react";
import SignUpForm from "./signup";


const SigninForm = (props) => {
    const [showSignup, setShowSignup] = useState(false);

    const toggleSignup = () => {
        setShowSignup(!showSignup);
    };

    return (
        <form className="dropdown-menu p-4">
          <div className="mb-3">
            <label className="form-label">Email address </label>
            <input
              type="email"
              className="form-control"
              placeholder="email@example.com"
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Password </label>
            <input
              type="password"
              className="form-control"
              placeholder="Password"
            />
          </div>
          <button type="submit" class="btn btn-primary" onClick={props.togglelog}>
            Sign in
          </button>
          <div className="dropdown-divider"></div>
          <p className="dropdown-item" onClick={toggleSignup}>New around here? Sign up</p>

        {showSignup && (
            <div className="signup-form-overlay">
            <div className="signup-form">
                <SignUpForm toggleSignup = {toggleSignup} togglelog = {props.togglelog}/>
            </div>
            </div>
        )}

          <a className="dropdown-item" href="#">Forgot password?</a>
        </form>
    );
};

export default SigninForm;
