import React, { useState, useEffect } from 'react';
import NewsList from './sections/NewsList';
import Footer from './components/Footer'; 
import Header from './components/Header';
import Topnews from './sections/Topnews';
import Menu from './sections/Menu';
import Showbookmarks from './sections/Bookmarks';
import UserProfile from './sections/profile';


const App = () => {

  const [endpnt, setendpoint] = useState('category/general');
  const [crntcontainer , setcontainer] = useState('main');
  const [userData, setUserData] = useState(null);

    useEffect(() => {
      const token = localStorage.getItem("token"); // Replace with the actual JWT token
      fetch('/api/protected', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
        .then((response) => response.json())
        .then((data) => setUserData(data))
        .catch((error) => console.error(error));
    }, []);

  return (
    <div>
    <div className='container'>
       <Header setedpt={setendpoint} setcontainer = {setcontainer}/>
      {crntcontainer === 'main' ? (
        <div >
          <Menu setedpt={setendpoint}/>
          <Topnews endpoint={endpnt}/>
          <NewsList endpoint={endpnt}/>
        </div>
      ) : (

        <div>
          {crntcontainer==='profile'&&(<UserProfile fullName = {userData.name} email = {userData.email} setcontainer = {setcontainer}/>)}
          {crntcontainer==='bookmarks'&&(<Showbookmarks bookmarks = {userData.bookmarks} />)}
        </div>
      )}
    </div>
    <Footer/>
    </div>
  );
};

export default App;
