import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Carousel } from 'react-bootstrap';

const Topnews = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/trending`)
      .then((response) => {
        setArticles(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div>
      {articles.length > 0 ? (
        <Carousel>
          {articles.map((article, index) => (
            <Carousel.Item key={index}>
              <img
                src={article.urlToImage || 'fallback-image.jpg'}
                alt={article.title}
                className="d-block w-100"
              />
              <Carousel.Caption>
                <h3>{article.title}</h3>
                <p>{article.description}</p>
                <a href={article.url} className="text-body-emphasis fw-bold">
                  Continue reading...
                </a>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
        </Carousel>
      ) : (
        <p>Loading...</p>
      )}
      <h3 className="pb-4 mb-4 fst-italic border-bottom">TRENDING</h3>
    </div>
  );
};

export default Topnews;
