import React from 'react';
import NewsList from './sections/NewsList';
import Footer from './components/Footer'; 
import Header from './components/Header';
import Topnews from './sections/Topnews';
import Menu from './sections/Menu';
import Showbookmarks from './sections/Bookmarks';
import UserProfile from './sections/profile';
import { useState } from 'react';


const App = () => {

  const [endpnt, setendpoint] = useState('category/general');
  const [crntcontainer , setcontainer] = useState('main');

  return (
    <div>
    <div className='container'>
       <Header setedpt={setendpoint} setcontainer = {setcontainer}/>
      {crntcontainer === 'main' ? (
        <div >
          <Menu setedpt={setendpoint}/>
          <Topnews endpoint={endpnt}/>
          <NewsList endpoint={endpnt}/>
        </div>
      ) : (

        <div>
          {crntcontainer==='profile'&&(<UserProfile fullName ='Ayushman' email = 'ayushmangarg@gmail.com' />)}
          {crntcontainer==='bookmarks'&&(<Showbookmarks/>)}
        </div>
      )}
    </div>
    <Footer/>
    </div>
  );
};

export default App;
