import React from "react";

const Links = (props) => {
  var toggle = 1;
  function handleClick(s,n) {
    props.setedpt(`category/${s}`);
    toggle = n;
  }
    return(
        <div className="nav-scroller py-1 mb-3 border-bottom">
          <nav className="nav nav-underline justify-content-between">
            <p className={`nav-item nav-link link-body-emphasis ${toggle===1&&"active"}`} onClick={()=>{handleClick('general',1)}}>
              Home
            </p>
            <p className={`nav-item nav-link link-body-emphasis ${toggle===2&&"active"}`} onClick={()=>{handleClick('technology',2)}}>
              Technology
            </p>
            <p className={`nav-item nav-link link-body-emphasis ${toggle===3&&"active"}`} onClick={()=>{handleClick('business',3)}}>
              Business
            </p>
            <p className={`nav-item nav-link link-body-emphasis ${toggle===4&&"active"}`} onClick={()=>{handleClick('entertainment',4)}}>
              Entertainment
            </p>
            <p className={`nav-item nav-link link-body-emphasis ${toggle===5&&"active"}`} onClick={()=>{handleClick('science',5)}}>
              Science
            </p>
            <p className={`nav-item nav-link link-body-emphasis ${toggle===6&&"active"}`} onClick={()=>{handleClick('health',6)}}>
              Health
            </p>
            <p className={`nav-item nav-link link-body-emphasis ${toggle===7&&"active"}`} onClick={()=>{handleClick('sports',7)}}>
              Sports
            </p>
          </nav>
        </div>
    );
};

export default Links;
