import React, { useState } from "react";
import LoadingSpinner from './LoadingSpinner';
import MenuItem from './components/MenuItem'; // Import the MenuItem component

const Links = (props) => {
  const [selectedLink, setSelectedLink] = useState('category/general');
  const [isLoading, setIsLoading] = useState(false);

  function handleClick(path) {
    setIsLoading(true);
    setSelectedLink(path);
    props.setedpt(path);

    // Simulate an API call delay for loading purposes (you can remove this in your actual code)
    setTimeout(() => {
      setIsLoading(false);
    }, 1000);
  }

  return (
    <div className="nav-scroller py-1 mb-3 border-bottom">
      {isLoading && <LoadingSpinner />}

      <nav className="nav nav-underline justify-content-between">
        <MenuItem
          selectedLink={selectedLink}
          onClick={handleClick}
          path="category/general"
          label="Home"
        />
        <MenuItem
          selectedLink={selectedLink}
          onClick={handleClick}
          path="search/world"
          label="World"
        />
      </nav>
    </div>
  );
};

export default Links;
